package training.lab1;

public class Conversiones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Conversion de short a double
		short age = 10;
		double myDouble = age;
		System.out.println("Casting de short a double: " + myDouble);
	
		// Conversion de un double a un int
		int x = (int) 20.9;
		System.out.println("Casting de double a int: " + x);
		
		// Conversion de un double a float
		float num = (float) 20.9;
		System.out.println("Casting de double a float: " + num);
		
	
	}

}
